mapper.delete:
update FREE
set deldt = sysdate
where fno = #{fno}

mapper.select:

select
	fno, id, title, content, regdt, upddt
from (
	select /*+INDEX_DESC(FREE pk_free) */
		rownum rn, fno, id, title, content, regdt, upddt
	from FREE
	where 





	rownum <= #{pageNum} * #{amount} and DELDT is null
)
where rn > (#{pageNum} -1) * #{amount}

mapper.update:

update FREE
set deldt = sysdate
where fno = #{fno}
