mapper.delete:
update FREE
set deldt = sysdate
where fno = #{fno}

mapper.select:

select
	fno, id, title, content, hit, thumb, regdt, upddt, replycnt
from (
	select /*+INDEX_DESC(FREE pk_free) */
		rownum rn, fno, id, title, content, hit, thumb, regdt, upddt, replycnt
	from FREE
	where 





	rownum <= #{pageNum} * #{amount} and DELDT is null
)
where rn > (#{pageNum} -1) * #{amount}

mapper.update:

update FREE
set deldt = sysdate
where fno = #{fno}

update FREE
set replycnt = replycnt + #{amount}
where fno = #{fno}

update FREE
set hit = hit + #{amount}
where fno = #{fno}

update FREE
set THUMB = THUMB + #{amount}
where FNO = #{fno}
